import React from 'react';
import './Results.css';

const CALENDLY_URL = process.env.REACT_APP_CALENDLY_URL || 'https://calendly.com/your-link';

function Results({ data, onReset }) {
  const { seo, ia, orientation, crawlData } = data;

  const getOrientationText = () => {
    switch (orientation) {
      case 'IA':
        return 'Votre site est mieux pr√©par√© pour la visibilit√© IA. Les moteurs conversationnels pourront mieux comprendre et citer votre contenu.';
      case 'SEO':
        return 'Votre site est mieux pr√©par√© pour la visibilit√© SEO traditionnelle. Les moteurs de recherche classiques pourront mieux explorer et classer vos pages.';
      case 'HYBRIDE':
        return 'Votre site pr√©sente un √©quilibre entre pr√©paration SEO et IA. Une approche hybride est recommand√©e pour maximiser votre visibilit√©.';
      default:
        return '';
    }
  };

  const getScoreColor = (score) => {
    if (score >= 70) return '#4CAF50';
    if (score >= 50) return '#C9AD01';
    return '#FF6B6B';
  };

  const getInterpretation = (score) => {
    if (score >= 80) return 'Excellent : Votre site est tr√®s bien pr√©par√©.';
    if (score >= 60) return 'Bon : Votre site est bien pr√©par√© avec quelques axes d\'am√©lioration.';
    if (score >= 40) return 'Moyen : Votre site n√©cessite des am√©liorations significatives.';
    return 'Faible : Votre site n√©cessite une refonte importante.';
  };

  return (
    <div className="results-container">
      <div className="results-header">
        <h2 className="results-title">R√©sultats de l'analyse</h2>
        <button onClick={onReset} className="reset-button">
          Nouvelle analyse
        </button>
      </div>

      <div className="results-grid">
        {seo && (
          <div className="score-card">
            <div className="score-header">
              <h3 className="score-label">Score SEO</h3>
              <div className="score-value" style={{ color: getScoreColor(seo.score) }}>
                {seo.score}/{seo.maxPossibleScore || 100}
              </div>
            </div>
            {seo.note && (
              <p className="score-note" style={{ fontSize: '0.85rem', color: '#999', fontStyle: 'italic', marginTop: '5px' }}>
                {seo.note}
              </p>
            )}
            <p className="score-interpretation">{getInterpretation(seo.score)}</p>

            <div className="score-details">
              <h4 className="details-title">D√©tail par axe</h4>
              {seo.details && (
                <div className="details-list">
                  {Object.entries(seo.details).map(([key, value]) => (
                    <div key={key} className="detail-item">
                      <span className="detail-label">{key}:</span>
                      <span className="detail-score">{value.score || 0} points</span>
                    </div>
                  ))}
                </div>
              )}
            </div>
          </div>
        )}

        {ia && (
          <div className="score-card">
            <div className="score-header">
              <h3 className="score-label">Score IA</h3>
              <div className="score-value" style={{ color: getScoreColor(ia.score) }}>
                {ia.score}/{ia.maxPossibleScore || 100}
              </div>
            </div>
            {ia.note && (
              <p className="score-note" style={{ fontSize: '0.85rem', color: '#999', fontStyle: 'italic', marginTop: '5px' }}>
                {ia.note}
              </p>
            )}
            <p className="score-interpretation">{getInterpretation(ia.score)}</p>

            <div className="score-details">
              <h4 className="details-title">D√©tail par axe</h4>
              {ia.details && (
                <div className="details-list">
                  {Object.entries(ia.details).map(([key, value]) => (
                    <div key={key} className="detail-item">
                      <span className="detail-label">{key}:</span>
                      <span className="detail-score">{value.score || 0} points</span>
                    </div>
                  ))}
                </div>
              )}
            </div>
          </div>
        )}
      </div>

      {orientation && (
        <div className="orientation-card">
          <h3 className="orientation-title">Orientation strat√©gique</h3>
          <div className="orientation-badge" data-orientation={orientation}>
            {orientation}
          </div>
          <p className="orientation-text">{getOrientationText()}</p>
        </div>
      )}

      {crawlData && (
        <div className="crawl-data-section">
          <h3 className="crawl-title">üìä Donn√©es analys√©es de votre site</h3>
          <p className="crawl-subtitle">Ces informations prouvent que votre site a √©t√© r√©ellement analys√©</p>

          <div className="crawl-stats-grid">
            <div className="stat-card">
              <div className="stat-value">{crawlData.pagesAnalyzed}</div>
              <div className="stat-label">Pages analys√©es</div>
            </div>
            <div className="stat-card">
              <div className="stat-value">{crawlData.statistics.totalWords.toLocaleString('fr-FR')}</div>
              <div className="stat-label">Mots au total</div>
            </div>
            <div className="stat-card">
              <div className="stat-value">{crawlData.statistics.averageWordsPerPage}</div>
              <div className="stat-label">Mots par page (moy.)</div>
            </div>
            <div className="stat-card">
              <div className="stat-value">{crawlData.statistics.totalInternalLinks}</div>
              <div className="stat-label">Liens internes</div>
            </div>
            <div className="stat-card">
              <div className="stat-value">{crawlData.statistics.totalExternalLinks}</div>
              <div className="stat-label">Liens externes</div>
            </div>
            <div className="stat-card">
              <div className="stat-value">{crawlData.statistics.pagesWithMetaDesc}/{crawlData.pagesAnalyzed}</div>
              <div className="stat-label">Pages avec meta description</div>
            </div>
          </div>

          <div className="pages-analyzed-section">
            <h4 className="pages-title">Pages analys√©es en d√©tail</h4>
            <div className="pages-list">
              {crawlData.pagesDetails.map((page, index) => (
                <div key={index} className="page-item">
                  <div className="page-header">
                    <a 
                      href={page.url} 
                      target="_blank" 
                      rel="noopener noreferrer"
                      className="page-url"
                    >
                      {page.url}
                    </a>
                  </div>
                  <div className="page-details">
                    <span className="page-title">üìÑ {page.title}</span>
                    <div className="page-metrics">
                      <span className="metric">{page.wordCount} mots</span>
                      <span className="metric">{page.h1Count} titre(s) H1</span>
                    </div>
                  </div>
                </div>
              ))}
            </div>
            {crawlData.pagesAnalyzed > crawlData.pagesDetails.length && (
              <p className="pages-note">
                + {crawlData.pagesAnalyzed - crawlData.pagesDetails.length} autre(s) page(s) analys√©e(s)
              </p>
            )}
          </div>

          <div className="technical-info-section">
            <h4 className="technical-title">Informations techniques d√©tect√©es</h4>
            <div className="technical-grid">
              <div className="technical-item">
                <span className="technical-label">Protocole:</span>
                <span className="technical-value">
                  {crawlData.protocol === 'HTTPS' ? (
                    <span style={{ color: '#4CAF50' }}>‚úÖ {crawlData.protocol}</span>
                  ) : (
                    <span style={{ color: '#FF6B6B' }}>‚ö†Ô∏è {crawlData.protocol}</span>
                  )}
                </span>
              </div>
              <div className="technical-item">
                <span className="technical-label">robots.txt:</span>
                <span className="technical-value">
                  {crawlData.robotsTxt.present ? (
                    <span style={{ color: '#4CAF50' }}>‚úÖ Pr√©sent</span>
                  ) : (
                    <span style={{ color: '#FF6B6B' }}>‚ùå Absent</span>
                  )}
                </span>
              </div>
              <div className="technical-item">
                <span className="technical-label">sitemap.xml:</span>
                <span className="technical-value">
                  {crawlData.sitemap.present ? (
                    <span style={{ color: '#4CAF50' }}>‚úÖ Pr√©sent</span>
                  ) : (
                    <span style={{ color: '#FF6B6B' }}>‚ùå Absent</span>
                  )}
                </span>
              </div>
              <div className="technical-item">
                <span className="technical-label">Donn√©es structur√©es:</span>
                <span className="technical-value">
                  {crawlData.statistics.pagesWithSchema > 0 ? (
                    <span style={{ color: '#4CAF50' }}>‚úÖ {crawlData.statistics.pagesWithSchema} page(s)</span>
                  ) : (
                    <span style={{ color: '#FF6B6B' }}>‚ùå Aucune</span>
                  )}
                </span>
              </div>
            </div>
          </div>
        </div>
      )}

      <div className="disclaimer">
        <p className="disclaimer-text">
          <strong>Important :</strong> Cet outil ne simule pas ChatGPT ni aucun moteur r√©el. 
          Il ne garantit ni classement Google, ni citation par une IA. 
          Il mesure uniquement une pr√©paration structurelle et informationnelle. 
          Les r√©sultats doivent √™tre interpr√©t√©s comme des indicateurs strat√©giques, et non comme des pr√©dictions.
        </p>
      </div>

      <div className="cta-section">
        <a
          href={CALENDLY_URL}
          target="_blank"
          rel="noopener noreferrer"
          className="calendly-button"
        >
          Voir comment corriger ces points
        </a>
      </div>
    </div>
  );
}

export default Results;
