import React, { useState, useEffect } from 'react';
import './Loading.css';

function Loading({ url }) {
  const [currentStep, setCurrentStep] = useState(0);

  const steps = [
    { text: 'Connexion au site', icon: '•' },
    { text: 'Analyse de la page d\'accueil', icon: '•' },
    { text: 'Découverte des pages', icon: '•' },
    { text: 'Crawl des pages', icon: '•' },
    { text: 'Extraction des données SEO', icon: '•' },
    { text: 'Extraction des données IA', icon: '•' },
    { text: 'Calcul des scores', icon: '•' },
    { text: 'Finalisation', icon: '•' }
  ];

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentStep((prev) => {
        if (prev < steps.length - 1) {
          return prev + 1;
        }
        return prev;
      });
    }, 800); // Change d'étape toutes les 800ms

    return () => clearInterval(interval);
  }, []);

  return (
    <div className="loading-container">
      <div className="loading-card">
        <div className="loading-spinner">
          <div className="spinner"></div>
        </div>
        <h3 className="loading-title">Analyse en cours</h3>
        <p className="loading-url">{url}</p>

        <div className="loading-steps">
          {steps.map((step, index) => (
            <div 
              key={index} 
              className={`loading-step ${index <= currentStep ? 'active' : ''} ${index < currentStep ? 'completed' : ''}`}
            >
              <span className="step-icon">{step.icon}</span>
              <span className="step-text">{step.text}</span>
              {index < currentStep && <span className="step-check">✓</span>}
            </div>
          ))}
        </div>

        <div className="loading-progress">
          <div 
            className="loading-progress-bar" 
            style={{ width: `${((currentStep + 1) / steps.length) * 100}%` }}
          ></div>
        </div>
        <p className="loading-hint">Cette analyse peut prendre jusqu'à 15 secondes...</p>
      </div>
    </div>
  );
}

export default Loading;
