import React, { useState, useEffect, useCallback } from 'react';
import counterService from '../services/counter';
import './Counter.css';

function Counter() {
  const [stats, setStats] = useState({ total: 0, today: 0, formatted: { total: '0', today: '0' } });
  const [loading, setLoading] = useState(true);
  const [isUpdating, setIsUpdating] = useState(false);

  const fetchStats = useCallback(async () => {
    try {
      setIsUpdating(true);
      const data = await counterService.getStats();

      // Si le total a augmentÃ©, on a une nouvelle analyse
      setStats((prevStats) => {
        const newTotal = data.total || 0;
        const prevTotal = prevStats.total || 0;

        if (newTotal > prevTotal && prevTotal > 0) {
          // Animation de cÃ©lÃ©bration
          console.log('[COUNTER] âœ… Nouvelle analyse dÃ©tectÃ©e !', prevTotal, 'â†’', newTotal);
        }

        return {
          total: newTotal,
          today: data.today || 0,
          formatted: {
            total: counterService.formatNumber(newTotal),
            today: (data.today || 0).toString()
          }
        };
      });
    } catch (error) {
      console.error('[COUNTER] Erreur:', error);
    } finally {
      setLoading(false);
      // Garder l'indicateur de mise Ã  jour un peu plus longtemps pour que l'utilisateur le voie
      setTimeout(() => setIsUpdating(false), 1500);
    }
  }, []);

  useEffect(() => {
    fetchStats();

    // Ã‰couter les Ã©vÃ©nements de mise Ã  jour du compteur
    const handleCounterUpdate = () => {
      console.log('[COUNTER] Mise Ã  jour demandÃ©e');
      fetchStats();
    };

    window.addEventListener('counter:update', handleCounterUpdate);

    // RafraÃ®chir toutes les 5 minutes
    const interval = setInterval(fetchStats, 5 * 60 * 1000);

    return () => {
      window.removeEventListener('counter:update', handleCounterUpdate);
      clearInterval(interval);
    };
  }, [fetchStats]);

  if (loading) {
    return (
      <div className="counter-container">
        <div className="counter-loading">...</div>
      </div>
    );
  }

  return (
    <div className={`counter-container ${isUpdating ? 'counter-updating' : ''}`}>
      <div className="counter-item">
        <span className="counter-label">Sites analysÃ©s</span>
        <span className="counter-value">{stats.formatted.total || stats.total}</span>
        {isUpdating && <span className="counter-update-indicator">ðŸ”„</span>}
      </div>
      {stats.today > 0 && (
        <div className="counter-item counter-today">
          <span className="counter-label">Aujourd'hui</span>
          <span className="counter-value">{stats.today}</span>
        </div>
      )}
    </div>
  );
}

export default Counter;
