import React, { useState, useEffect } from 'react';
import './App.css';
import Form from './components/Form';
import Results from './components/Results';
import Loading from './components/Loading';
import Header from './components/Header';
import Footer from './components/Footer';
import CalendlyModal from './components/CalendlyModal';
import LegalModal from './components/LegalModal';

function App() {
  const [results, setResults] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [analyzingUrl, setAnalyzingUrl] = useState(null);

  const handleAnalyze = async (formData) => {
    setLoading(true);
    setError(null);
    setResults(null);
    setAnalyzingUrl(formData.url);

    try {
      const response = await fetch('/api/analyze', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Erreur lors de l\'analyse');
      }

      const data = await response.json();
      setResults(data);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
      setAnalyzingUrl(null);
    }
  };

  const handleReset = () => {
    setResults(null);
    setError(null);
    setAnalyzingUrl(null);
  };

  return (
    <div className="App">
      <Header />
      <main className={`main-content ${!results && !loading ? 'no-scroll' : ''}`}>
        {loading ? (
          <Loading url={analyzingUrl} />
        ) : !results ? (
          <div className="fade-in">
            <Form onSubmit={handleAnalyze} loading={loading} error={error} />
          </div>
        ) : (
          <div className="fade-in">
            <Results data={results} onReset={handleReset} />
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
